        :class="themeClass"
        :columnDefs="columnDefs"
        @grid-ready="onGridReady"
        :defaultColDef="defaultColDef"
        :rowModelType="rowModelType"></ag-grid-vue>
      </div>
        </div>
    `,
    components: {
        'ag-grid-vue': AgGridVue,

    },
    data: function() {
        return {
            columnDefs: [{field:"athlete",
colId:"athlete",
minWidth:180},{field:"age",
