import Vue from 'vue';
import { AgGridVue } from '@ag-grid-community/vue';
import '@ag-grid-community/styles/ag-grid.css';
import "@ag-grid-community/styles/ag-theme-quartz.css";
import './style.css';
import { ClientSideRowModelModule } from '@ag-grid-community/client-side-row-model';
import { GridChartsModule } from '@ag-grid-enterprise/charts-enterprise';
import { MenuModule } from '@ag-grid-enterprise/menu';
import { RowGroupingModule } from '@ag-grid-enterprise/row-grouping';
import { ModuleRegistry } from "@ag-grid-community/core";
ModuleRegistry.registerModules([ClientSideRowModelModule, GridChartsModule, MenuModule, RowGroupingModule]);





const VueExample = {
    template: `
        <div style="height: 100%">
                <div class="wrapper">
      <ag-grid-vue
        style="width: 100%; height: 100%;"
        :class="themeClass"
        :columnDefs="columnDefs"
        @grid-ready="onGridReady"
        :defaultColDef="defaultColDef"
        :enableRangeSelection="true"
        :popupParent="popupParent"
        :enableCharts="true"
        :chartThemeOverrides="chartThemeOverrides"
        :rowData="rowData"
        @first-data-rendered="onFirstDataRendered"></ag-grid-vue>
        <div id="myChart" class="ag-theme-quartz my-chart"></div>
      </div>
        </div>
    `,
    components: {
        'ag-grid-vue': AgGridVue,

    },
    data: function() {
        return {
            columnDefs: [{field:"athlete",
width:150,
chartDataType:"category"},{field:"age",
chartDataType:"category",
sort:"asc"},{field:"sport"},{field:"year",
chartDataType:"excluded"},{field:"gold",
chartDataType:"series"},{field:"silver",
chartDataType:"series"},{field:"bronze"}],
            gridApi: null,
            themeClass: /** DARK MODE START **/document.documentElement.dataset.defaultTheme || 'ag-theme-quartz'/** DARK MODE END **/,
            defaultColDef: {
    flex: 1
},
            popupParent: null,
chartThemeOverrides: null,
rowData: null
        }
    },
    created() {
        this.popupParent = document.body;
this.chartThemeOverrides = {
    common: {
        title: {
            enabled: true,
            text: 'Medals by Age',
        },
    },
    bar: {
        axes: {
            category: {
                label: {
                    rotation: 0,
                },
            },
        },
    },
}        
    },
    methods: {
        onFirstDataRendered(params) {
    params.api.createRangeChart({
        chartContainer: document.querySelector('#myChart'),
        cellRange: {
            rowStartIndex: 0,
            rowEndIndex: 79,
            columns: ['age', 'gold', 'silver', 'bronze'],
        },
        chartType: 'groupedColumn',
        aggFunc: 'sum',
    });
},
onGridReady(params) {
        /** DARK INTEGRATED START **/
    const isInitialModeDark = () => {
            const attr = document.documentElement.getAttribute('data-default-theme');
            return attr ? attr.endsWith('-dark') : false;
        };

        const updateChartThemes = (isDark) => {           
            const themes = ['ag-default', 'ag-material', 'ag-sheets', 'ag-polychroma', 'ag-vivid'];            
            const currentThemes = params.api.getGridOption('chartThemes');                    
            const customTheme = currentThemes && currentThemes.some(theme => theme.startsWith('my-custom-theme'));

            let modifiedThemes = customTheme
                ? (isDark ? ['my-custom-theme-dark', 'my-custom-theme-light'] : ['my-custom-theme-light', 'my-custom-theme-dark'])
                : Array.from(new Set(themes.map((theme) => theme + (isDark ? '-dark' : ''))));                      

            // updating the 'chartThemes' grid option will cause the chart to reactively update!
            params.api.setGridOption('chartThemes', modifiedThemes);
        };

        // update chart themes when example first loads
        updateChartThemes(isInitialModeDark());

        const handleColorSchemeChange = (event) => {
            const { darkMode } = event.detail;
            updateChartThemes(darkMode);
        }

        // listen for user-triggered dark mode changes (not removing listener is fine here!)
        document.addEventListener('color-scheme-change', handleColorSchemeChange);
    /** DARK INTEGRATED END **/
        this.gridApi = params.api;



            const updateData = (data) => {
    this.rowData = data;
};

            fetch('https://www.ag-grid.com/example-assets/wide-spread-of-sports.json')
                .then(resp => resp.json())
                .then(data => updateData(data));
    },
    }
}



new Vue({
    el: '#app',
    components: {
        'my-component': VueExample
    }
});

