files.customLoadingOverlay.js:
class CustomLoadingOverlay  {


    init(params) {
        this.eGui = document.createElement('div');
        this.eGui.innerHTML =
            `<div class="ag-overlay-loading-center" role="presentation">
               <div role="presentation" style="height:100px; width:100px; background: url(https://ag-grid.com/images/ag-grid-loading-spinner.svg) center / contain no-repeat; margin: 0 auto;"></div>
               <div aria-live="polite" aria-atomic="true">${params.loadingMessage}</div>
            </div>`;
    }

    getGui() {
        return this.eGui;
    }

    refresh(params) {
        return false;
    }
}

files.customNoRowsOverlay.js:
class CustomNoRowsOverlay  {


    init(params) {
        this.eGui = document.createElement('div');
        this.eGui.innerHTML = `
            <div role="presentation" class="ag-overlay-loading-center" style="background-color: #b4bebe;">
                <i class="far fa-frown" aria-live="polite" aria-atomic="true"> ${params.noRowsMessageFunc()} </i>
            </div>
        `;
    }

    getGui() {
        return this.eGui;
    }

    refresh(params) {
        return false;
    }
}

files.main.js:
const columnDefs = [
  { field: 'athlete', width: 150 },
  { field: 'age', width: 90 },
  { field: 'country', width: 120 },
  { field: 'year', width: 90 },
  { field: 'date', width: 110 },
  { field: 'sport', width: 110 },
  { field: 'gold', width: 100 },
  { field: 'silver', width: 100 },
  { field: 'bronze', width: 100 },
  { field: 'total', width: 100 },
]

let gridApi;

const gridOptions = {
  defaultColDef: {
    editable: true,
    flex: 1,
    minWidth: 100,
    filter: true,
  },

  // set rowData to null or undefined to show loading panel by default
  rowData: null,
  columnDefs: columnDefs,

  loadingOverlayComponent: CustomLoadingOverlay,
  loadingOverlayComponentParams: {
    loadingMessage: 'One moment please...',
  },
  noRowsOverlayComponent: CustomNoRowsOverlay,
  noRowsOverlayComponentParams: {
    noRowsMessageFunc: () => 'No rows found at: ' + new Date().toLocaleTimeString(),
  },
}

function onBtShowLoading() {
  gridApi.showLoadingOverlay()
}

function onBtShowNoRows() {
  gridApi.showNoRowsOverlay()
}

function onBtHide() {
  gridApi.hideOverlay()
}

// setup the grid after the page has finished loading
document.addEventListener('DOMContentLoaded', () => {
  const gridDiv = document.querySelector('#myGrid')
  gridApi = agGrid.createGrid(gridDiv, gridOptions); 

  fetch('https://www.ag-grid.com/example-assets/olympic-winners.json')
    .then(response => response.json())
    .then(data => {
      gridApi.setGridOption('rowData', data)
    })
})
