let gridApi;

const cellClassRules = {
    'very-low-cost': (p) => { return p.value < 2500000},
    'low-cost': (p) => { return p.value > 2500000 && p.value < 5000000},
    'medium-cost': (p) => { return p.value > 5000000 && p.value < 7500000},
    'high-cost': (p) => { return p.value > 7500000 && p.value < 9000000},
    'very-high-cost': (p) => { return p.value >= 9000000},
}

const rowClassRules = {
    'unsucessful-mission': (p) => { return p.data.successful === false },
    'successful-mission': (p) => { return p.data.successful === true }
}

const gridOptions = {
    // Data to be displayed
    rowData: [
        { company: "RVSN USSR", country: "Kazakhstan", date: "1957-10-04", mission: "Sputnik-1", price: 9550000, successful: true },
        { company: "RVSN USSR", country: "Kazakhstan", date: "1957-11-03", mission: "Sputnik-2", price: 8990000, successful: true },
        { company: "US Navy", country: "USA", date: "1957-12-06", mission: "Vanguard TV3", price: 6860000, successful: false }
    ],
    // Columns to be displayed (Should match rowData properties)
    columnDefs: [
        {
            field: "mission",
            cellClass: 'mission-cell'
        },
        {
            field: "country"
        },
        {
            field: "successful",
        },
        {
            field: "date"
        },
        {
            field: "price",
            cellClassRules: cellClassRules
        },
        {
            field: "company"
        }
    ],
    // Configurations applied to all columns
    defaultColDef: {
        filter: true,
        editable: true,
    },
    // Grid Options & Callbacks
    pagination: true,
    rowClass: 'row',
    rowSelection: 'multiple',
    rowClassRules: rowClassRules
}
document.addEventListener('DOMContentLoaded', function () {
    var gridDiv = document.querySelector('#myGrid');
    gridApi = agGrid.createGrid(gridDiv, gridOptions); 
    fetch('https://www.ag-grid.com/example-assets/space-mission-data.json')
        .then(response => response.json())
        .then((data) => gridApi.setGridOption('rowData', data))
})
