eventHandlers.handler:
function onFirstDataRendered(params) {
    params.api.createCrossFilterChart({
        chartType: 'pie',
        cellRange: {
            columns: [
                'salesRep',
                'sale'
            ]
        },
        aggFunc: 'sum',
        chartThemeOverrides: {
            common: {
                title: {
                    enabled: true,
                    text: 'Sales by Representative ($)'
                }
            },
            pie: {
                series: {
                    title: { enabled: false },
                    label: { enabled: false }
                }
            }
        },
        chartContainer: document.querySelector('#pieChart')
    });
}

