utils:
function getRowData() {
    var rowData = [];
    for (var i = 1; i <= 10; i++) {
        rowData.push({
            group: i < 5 ? 'A' : 'B',
            a: i * 863 % 100,
            b: i * 811 % 100,
            c: i * 743 % 100,
            d: i * 677 % 100
        });
    }
    return rowData;
}
