externalEventHandlers.body:
function expandAll(expand) {
    const columnApi = this.gridColumnApi;
    const groupNames = [
        'GroupA',
        'GroupB',
        'GroupC',
        'GroupD',
        'GroupE',
        'GroupF',
        'GroupG'
    ];
    groupNames.forEach(groupId => {
        columnApi.setColumnGroupOpened(groupId, expand);
    });
}

utils:
function headerClassFunc(params) {
    let foundC = false;
    let foundG = false;
    let item = params.column ? params.column : params.columnGroup;
    while (item) {
        const colDef = item.getDefinition();
        if (colDef.groupId === 'GroupC') {
            foundC = true;
        } else if (colDef.groupId === 'GroupG') {
            foundG = true;
        }
        item = item.getParent();
    }
    if (foundG) {
        return 'column-group-g';
    } else if (foundC) {
        return 'column-group-c';
    }
}
