externalEventHandlers.body:
function onBtSortOn() {
    this.gridColumnApi.applyColumnState({
        state: [
            {
                colId: 'age',
                sort: 'desc'
            },
            {
                colId: 'athlete',
                sort: 'asc'
            }
        ]
    });
}

function onBtSortOff() {
    this.gridColumnApi.applyColumnState({ defaultState: { sort: null } });
}

function onBtWidthNarrow() {
    this.gridColumnApi.applyColumnState({
        state: [
            {
                colId: 'age',
                width: 100
            },
            {
                colId: 'athlete',
                width: 100
            }
        ]
    });
}

function onBtWidthNormal() {
    this.gridColumnApi.applyColumnState({
        state: [
            {
                colId: 'age',
                width: 200
            },
            {
                colId: 'athlete',
                width: 200
            }
        ]
    });
}

function onBtHide() {
    this.gridColumnApi.applyColumnState({
        state: [
            {
                colId: 'age',
                hide: true
            },
            {
                colId: 'athlete',
                hide: true
            }
        ]
    });
}

function onBtShow() {
    this.gridColumnApi.applyColumnState({ defaultState: { hide: false } });
}

function onBtPivotOn() {
    this.gridColumnApi.setPivotMode(true);
    this.gridColumnApi.applyColumnState({
        state: [{
                colId: 'country',
                pivot: true
            }]
    });
}

function onBtPivotOff() {
    this.gridColumnApi.setPivotMode(false);
    this.gridColumnApi.applyColumnState({ defaultState: { pivot: false } });
}

function onBtRowGroupOn() {
    this.gridColumnApi.applyColumnState({
        state: [{
                colId: 'sport',
                rowGroup: true
            }]
    });
}

function onBtRowGroupOff() {
    this.gridColumnApi.applyColumnState({ defaultState: { rowGroup: false } });
}

function onBtAggFuncOn() {
    this.gridColumnApi.applyColumnState({
        state: [
            {
                colId: 'gold',
                aggFunc: 'sum'
            },
            {
                colId: 'silver',
                aggFunc: 'sum'
            },
            {
                colId: 'bronze',
                aggFunc: 'sum'
            }
        ]
    });
}

function onBtAggFuncOff() {
    this.gridColumnApi.applyColumnState({ defaultState: { aggFunc: null } });
}

function onBtNormalOrder() {
    this.gridColumnApi.applyColumnState({
        state: [
            { colId: 'athlete' },
            { colId: 'age' },
            { colId: 'country' },
            { colId: 'sport' },
            { colId: 'gold' },
            { colId: 'silver' },
            { colId: 'bronze' }
        ],
        applyOrder: true
    });
}

function onBtReverseOrder() {
    this.gridColumnApi.applyColumnState({
        state: [
            { colId: 'athlete' },
            { colId: 'age' },
            { colId: 'country' },
            { colId: 'sport' },
            { colId: 'bronze' },
            { colId: 'silver' },
            { colId: 'gold' }
        ],
        applyOrder: true
    });
}

function onBtPinnedOn() {
    this.gridColumnApi.applyColumnState({
        state: [
            {
                colId: 'athlete',
                pinned: 'left'
            },
            {
                colId: 'age',
                pinned: 'right'
            }
        ]
    });
}

function onBtPinnedOff() {
    this.gridColumnApi.applyColumnState({ defaultState: { pinned: null } });
}
