externalEventHandlers.body:
function onBtSortAthlete() {
    this.gridColumnApi.applyColumnState({
        state: [{
                colId: 'athlete',
                sort: 'asc'
            }]
    });
}

function onBtSortCountryThenSportClearOthers() {
    this.gridColumnApi.applyColumnState({
        state: [
            {
                colId: 'country',
                sort: 'asc',
                sortIndex: 0
            },
            {
                colId: 'sport',
                sort: 'asc',
                sortIndex: 1
            }
        ],
        defaultState: { sort: null }
    });
}

function onBtClearAllSorting() {
    this.gridColumnApi.applyColumnState({ defaultState: { sort: null } });
}

function onBtRowGroupCountryThenSport() {
    this.gridColumnApi.applyColumnState({
        state: [
            {
                colId: 'country',
                rowGroupIndex: 0
            },
            {
                colId: 'sport',
                rowGroupIndex: 1
            }
        ],
        defaultState: { rowGroup: false }
    });
}

function onBtRemoveCountryRowGroup() {
    this.gridColumnApi.applyColumnState({
        state: [{
                colId: 'country',
                rowGroup: false
            }]
    });
}

function onBtClearAllRowGroups() {
    this.gridColumnApi.applyColumnState({ defaultState: { rowGroup: false } });
}

function onBtOrderColsMedalsFirst() {
    this.gridColumnApi.applyColumnState({
        state: [
            { colId: 'gold' },
            { colId: 'silver' },
            { colId: 'bronze' },
            { colId: 'total' },
            { colId: 'athlete' },
            { colId: 'age' },
            { colId: 'country' },
            { colId: 'sport' },
            { colId: 'year' },
            { colId: 'date' }
        ],
        applyOrder: true
    });
}

function onBtOrderColsMedalsLast() {
    this.gridColumnApi.applyColumnState({
        state: [
            { colId: 'athlete' },
            { colId: 'age' },
            { colId: 'country' },
            { colId: 'sport' },
            { colId: 'year' },
            { colId: 'date' },
            { colId: 'gold' },
            { colId: 'silver' },
            { colId: 'bronze' },
            { colId: 'total' }
        ],
        applyOrder: true
    });
}

function onBtHideMedals() {
    this.gridColumnApi.applyColumnState({
        state: [
            {
                colId: 'gold',
                hide: true
            },
            {
                colId: 'silver',
                hide: true
            },
            {
                colId: 'bronze',
                hide: true
            },
            {
                colId: 'total',
                hide: true
            }
        ]
    });
}

function onBtShowMedals() {
    this.gridColumnApi.applyColumnState({
        state: [
            {
                colId: 'gold',
                hide: false
            },
            {
                colId: 'silver',
                hide: false
            },
            {
                colId: 'bronze',
                hide: false
            },
            {
                colId: 'total',
                hide: false
            }
        ]
    });
}
