externalEventHandlers.body:
function onBtExport() {
    const spreadsheets = [];
    const filterInstance = this.gridApi.getFilterInstance('athlete');
    filterInstance.setModel({ values: [] });
    this.gridApi.onFilterChanged();
    spreadsheets.push(this.gridApi.getSheetDataForExcel({
        prependContent: [
            [{
                    styleId: 'coverHeading',
                    mergeAcross: 3,
                    data: {
                        value: 'AG Grid',
                        type: 'String'
                    }
                }],
            [{
                    styleId: 'coverHeading',
                    mergeAcross: 3,
                    data: {
                        value: '',
                        type: 'String'
                    }
                }],
            [{
                    styleId: 'coverText',
                    mergeAcross: 3,
                    data: {
                        value: 'Data shown lists Olympic medal winners for years 2000-2012',
                        type: 'String'
                    }
                }],
            [{
                    styleId: 'coverText',
                    data: {
                        value: 'This data includes a row for each participation record - athlete name, country, year, sport, count of gold, silver, bronze medals won during the sports event',
                        type: 'String'
                    }
                }]
        ],
        processHeaderCallback: () => '',
        sheetName: 'cover'
    }));
    filterInstance.setModel(null);
    this.gridApi.onFilterChanged();
    spreadsheets.push(this.gridApi.getSheetDataForExcel());
    this.gridApi.exportMultipleSheetsAsExcel({
        data: spreadsheets,
        fileName: 'ag-grid.xlsx'
    });
}
