{
        addImageToCell: function (rowIndex, col, value) {
            if (col.getColId() !== "country") {
                return;
            }
            var countryCode = countryCodes[value];
            return {
                image: {
                    id: countryCode,
                    base64: base64flags[countryCode],
                    imageType: "png",
                    width: 20,
                    height: 11,
                    position: {
                        offsetX: 10,
                        offsetY: 5.5
                    }
                },
                value
            };
        }
    }

