properties.value:
{
    prependContent: [[{
                data: {
                    type: 'String',
                    value: logos.AgGrid
                },
                mergeAcross: 1
            }]],
    rowHeight: params => params.rowIndex === 1 ? 82 : 20,
    addImageToCell: (rowIndex, col, value) => {
        if (rowIndex !== 1 || col.getColId() !== 'athlete') {
            return;
        }
        return {
            image: {
                id: 'logo',
                base64: value,
                imageType: 'png',
                width: 295,
                height: 100,
                position: { colSpan: 2 }
            }
        };
    }
}
