externalEventHandlers.body:
function onBtExport() {
    var spreadsheets = [];
    this.gridApi.forEachNode((node, index) => {
        if (index % 100 === 0) {
            this.gridApi.deselectAll();
        }
        node.setSelected(true);
        if (index % 100 === 99) {
            spreadsheets.push(this.gridApi.getSheetDataForExcel({ onlySelected: true }));
        }
    });
    if (this.gridApi.getSelectedNodes().length) {
        spreadsheets.push(this.gridApi.getSheetDataForExcel({ onlySelected: true }));
        this.gridApi.deselectAll();
    }
    this.gridApi.exportMultipleSheetsAsExcel({ data: spreadsheets });
}
