externalEventHandlers.body:
function selectJohnAndKenny() {
    const instance = this.gridApi.getFilterInstance('athlete');
    instance.setModel({
        values: [
            'John Joe Nevin',
            'Kenny Egan'
        ]
    });
    this.gridApi.onFilterChanged();
}

function selectEverything() {
    const instance = this.gridApi.getFilterInstance('athlete');
    instance.setModel(null);
    this.gridApi.onFilterChanged();
}

function selectNothing() {
    const instance = this.gridApi.getFilterInstance('athlete');
    instance.setModel({ values: [] });
    this.gridApi.onFilterChanged();
}

function setCountriesToFranceAustralia() {
    const instance = this.gridApi.getFilterInstance('country');
    instance.setFilterValues([
        'France',
        'Australia'
    ]);
    instance.applyModel();
    this.gridApi.onFilterChanged();
}

function setCountriesToAll() {
    const instance = this.gridApi.getFilterInstance('country');
    instance.resetFilterValues();
    instance.applyModel();
    this.gridApi.onFilterChanged();
}

utils:
function patchData(data) {
    data.forEach(function (row) {
        const countryName = row.country;
        const countryCode = countryName.substring(0, 2).toUpperCase();
        row.country = {
            name: countryName,
            code: countryCode
        };
    });
}
