function changeSelection(type) {
    if (type === 'normal') {
        this.gridApi.setGroupRemoveSingleChildren(false);
        this.gridApi.setGroupRemoveLowestSingleChildren(false);
    } else if (type === 'single') {
        this.gridApi.setGroupRemoveSingleChildren(true);
        this.gridApi.setGroupRemoveLowestSingleChildren(false);
    } else if (type === 'lowest') {
        this.gridApi.setGroupRemoveLowestSingleChildren(true);
        this.gridApi.setGroupRemoveSingleChildren(false);
    } else {
        console.log('unknown type: ' + type);
    }
}
