function onChart1() {
    var params = {
        cellRange: {
            rowStartIndex: 0,
            rowEndIndex: 4,
            columns: [
                'country',
                'gold',
                'silver'
            ]
        },
        chartType: 'groupedColumn',
        chartThemeName: 'ag-vivid',
        chartThemeOverrides: {
            common: {
                title: {
                    enabled: true,
                    text: 'Top 5 Medal Winners'
                }
            }
        }
    };
    this.gridApi.createRangeChart(params);
}

function onChart2() {
