        cellRange: {
            columns: [
                'country',
                'bronze'
            ]
        },
        chartType: 'groupedBar',
        chartThemeName: 'ag-pastel',
        chartThemeOverrides: {
            common: {
                title: {
                    enabled: true,
                    text: 'Bronze Medal by Country'
                },
                legend: { enabled: false }
            }
        },
        unlinkChart: true
    };
    this.gridApi.createRangeChart(params);
}
