function createQuarterlySalesChart(gridApi) {
    gridApi.createCrossFilterChart({
        chartType: 'column',
        cellRange: {
            columns: [
                'quarter',
                'sale'
            ]
        },
        aggFunc: 'sum',
        chartThemeOverrides: {
            common: {
                title: {
                    enabled: true,
                    text: 'Quarterly Sales ($)'
                },
                legend: { enabled: false },
                axes: {
                    category: { label: { rotation: 0 } },
                    number: {
                        label: {
                            formatter: function (params) {
                                return params.value / 1000 + 'k';
                            }
                        }
                    }
                }
            }
        },
        chartContainer: document.querySelector('#columnChart')
    });
}

function createSalesByRefChart(gridApi) {
    gridApi.createCrossFilterChart({
        chartType: 'pie',
        cellRange: {
            columns: [
                'salesRep',
                'sale'
            ]
        },
        aggFunc: 'sum',
        chartThemeOverrides: {
            common: {
                title: {
                    enabled: true,
                    text: 'Sales by Representative ($)'
                }
            },
            pie: {
                series: {
                    title: { enabled: false },
                    label: { enabled: false }
                }
            }
        },
        chartContainer: document.querySelector('#pieChart')
    });
}

function createHandsetSalesChart(gridApi) {
    gridApi.createCrossFilterChart({
        chartType: 'bar',
        cellRange: {
            columns: [
                'handset',
                'sale'
            ]
        },
        aggFunc: 'count',
        chartThemeOverrides: {
            common: {
                title: {
                    enabled: true,
                    text: 'Handsets Sold (Units)'
                },
                legend: { enabled: false }
            }
        },
        chartContainer: document.querySelector('#barChart')
    });
}
