function onFirstDataRendered(params) {
    setTimeout(function () {
        var node1 = params.api.getDisplayedRowAtIndex(1);
        var node2 = params.api.getDisplayedRowAtIndex(2);
        node1.setExpanded(true);
        node2.setExpanded(true);
    }, 0);
}

