        if (nameMatch) {
            res.detailGridOptions = {
                columnDefs: [
                    { field: "callId" },
                    { field: "number" }
                ],
                defaultColDef: { flex: 1 }
            };
        } else {
            res.detailGridOptions = {
                columnDefs: [
                    { field: "callId" },
                    { field: "direction" },
                    {
                        field: "duration",
                        valueFormatter: "x.toLocaleString() + 's'"
                    },
                    { field: "switchCode" }
                ],
                defaultColDef: { flex: 1 }
            };
        }
        return res;
    }

