function addPivotColumn() {
    this.gridColumnApi.applyColumnState({
        state: [{
                colId: 'country',
                pivot: true
            }],
        defaultState: { pivot: false }
    });
}

function addPivotColumns() {
    this.gridColumnApi.applyColumnState({
        state: [
            {
                colId: 'year',
                pivot: true
            },
            {
                colId: 'country',
                pivot: true
            }
        ],
        defaultState: { pivot: false }
    });
}

function removePivotColumn() {
    this.gridColumnApi.applyColumnState({
        state: [{
                colId: 'country',
                pivot: false
            }]
    });
}

function emptyPivotColumns() {
    this.gridColumnApi.applyColumnState({ defaultState: { pivot: false } });
}
