function filterSwimming() {
    this.gridApi.setFilterModel({
        sport: {
            type: 'set',
            values: ['Swimming']
        }
    });
}

function ages16And20() {
    this.gridApi.setFilterModel({
        age: {
            type: 'set',
            values: [
                '16',
                '20'
            ]
        }
    });
}
