function sortByAthleteAsc() {
    this.gridColumnApi.applyColumnState({
        state: [{
                colId: 'athlete',
                sort: 'asc'
            }],
        defaultState: { sort: null }
    });
}

function sortByAthleteDesc() {
    this.gridColumnApi.applyColumnState({
        state: [{
                colId: 'athlete',
                sort: 'desc'
            }],
        defaultState: { sort: null }
    });
}

function sortByCountryThenSport() {
    this.gridColumnApi.applyColumnState({
        state: [
            {
                colId: 'country',
                sort: 'asc',
                sortIndex: 0
            },
            {
                colId: 'sport',
                sort: 'asc',
                sortIndex: 1
            }
        ],
        defaultState: { sort: null }
    });
}

function sortBySportThenCountry() {
    this.gridColumnApi.applyColumnState({
        state: [
            {
                colId: 'country',
                sort: 'asc',
                sortIndex: 1
            },
            {
                colId: 'sport',
                sort: 'asc',
                sortIndex: 0
            }
        ],
        defaultState: { sort: null }
    });
}

function clearSort() {
    this.gridColumnApi.applyColumnState({ defaultState: { sort: null } });
}

function saveSort() {
    var colState = this.gridColumnApi.getColumnState();
    var sortState = colState.filter(function (s) {
        return s.sort != null;
    }).map(function (s) {
        return {
            colId: s.colId,
            sort: s.sort,
            sortIndex: s.sortIndex
        };
    });
    savedSort = sortState;
    console.log('saved sort', sortState);
}

function restoreFromSave() {
    this.gridColumnApi.applyColumnState({
        state: savedSort,
        defaultState: { sort: null }
    });
}
