externalEventHandlers.body:
function redrawTopRows() {
    progressColor();
    var rows = [];
    for (var i = 0; i < 6; i++) {
        var row = this.gridApi.getDisplayedRowAtIndex(i);
        rows.push(row);
    }
    this.gridApi.redrawRows({ rowNodes: rows });
}

utils:
function createData(count) {
    var result = [];
    for (var i = 1; i <= count; i++) {
        result.push({
            a: i * 863 % 100,
            b: i * 811 % 100,
            c: i * 743 % 100,
            d: i * 677 % 100,
            e: i * 619 % 100,
            f: i * 571 % 100
        });
    }
    return result;
}

function progressColor() {
    colorIndex++;
    if (colorIndex === colors.length) {
        colorIndex = 0;
    }
}
